CREATE TABLE Automobiles (_id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, AutoModel TEXT NOT NULL, _idBrand INTEGER NOT NULL, Photo TEXT, ProductYear INTEGER CONSTRAINT YearCK CHECK (ProductYear > 0), Seats INTEGER CONSTRAINT SeatsCK CHECK (Seats > 0), _idBodyStyle INTEGER, FuelType TEXT, Transmission TEXT, Price REAL CONSTRAINT PriceCK CHECK (Price >= 0.0), CONSTRAINT AutoBrandFK FOREIGN KEY (_idBrand) REFERENCES Brands (_id), CONSTRAINT AutoBodyStyleFK FOREIGN KEY (_idBodyStyle) REFERENCES BodyStyles (_id));